	public void delChange(String modelids) throws SubjectChangeException {
		// TODO Auto-generated method stub

	}
	
	private void setSubSubject(String subid) throws SubjectChangeException{
		
		SubjectForm form = subjectDaoImp.load(subid);
		SubjectSubForm formsub = copyObj(form);
		subSubjectDaoImp.delete(getModelids(),getDtId());
		if(!subSubjectDaoImp.checkBossid(form.getSjId(),getDtId())) {
			//System.out.println(i++);
			subSubjectDaoImp.insert(formsub);
		} else {
			formsub.setSjId(subSubjectDaoImp.getIdByBossAndDt(form.getSjId(),getDtId()));
			subSubjectDaoImp.update(formsub);
		}
		
	}
	
	private SubjectSubForm copyObj(SubjectForm form) {
		SubjectSubForm formsub = new SubjectSubForm();
		formsub.setBossid(form.getSjId());
		formsub.setName(form.getName());
		formsub.setDtid(getDtId());
		
		return formsub;
	}

	public String getDtId() {
		return dtId;
	}

	public void setDtId(String dtId) {
		this.dtId = dtId;
	}
	

	public String getModelids() {
		return modelids;
	}

	public void setModelids(String modelids) {
		this.modelids = modelids;
	}
	
